import React, { useState, useRef } from 'react';
import aiService from './services/aiService';

const LoadingSpinner = () => (
  <div className="flex items-center justify-center">
    <div className="relative">
      <div className="w-16 h-16 border-4 border-gray-200 rounded-full animate-pulse"></div>
      <div className="absolute inset-0 w-16 h-16 border-4 border-accent border-t-transparent rounded-full animate-spin"></div>
    </div>
  </div>
);

const SettingCard = ({ title, value, explanation, isUpdating, confidence }) => (
  <div className={`bg-white rounded-xl shadow-lg p-8 transition-all duration-500 ${isUpdating ? 'scale-105 ring-2 ring-accent' : ''}`}>
    <div className="text-center">
      <h3 className="text-lg font-semibold text-gray-800 mb-3">{title}</h3>
      <div className={`text-4xl font-bold text-accent mb-4 transition-all duration-300 ${isUpdating ? 'animate-pulse' : ''}`}>
        {value}
      </div>
      <p className="text-sm text-gray-600 leading-relaxed mb-2">{explanation}</p>
      {confidence && (
        <div className="text-xs text-gray-500">
          Confidence: {Math.round(confidence * 100)}%
        </div>
      )}
    </div>
  </div>
);

const AIConfigurationBanner = ({ onConfigure }) => (
  <div className="bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200 rounded-xl p-6 mb-8">
    <div className="flex items-center justify-between">
      <div className="flex items-center">
        <div className="text-blue-600 text-2xl mr-4">🤖</div>
        <div>
          <h3 className="text-lg font-semibold text-blue-900">AI-Powered Analysis</h3>
          <p className="text-blue-700 text-sm">
            Configure your OpenAI API key to enable intelligent image analysis and natural language refinement.
          </p>
        </div>
      </div>
      <button
        onClick={onConfigure}
        className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg transition-colors"
      >
        Configure AI
      </button>
    </div>
  </div>
);

const App = () => {
  const [currentStep, setCurrentStep] = useState('landing'); // landing, analyzing, results, refining
  const [uploadedImage, setUploadedImage] = useState(null);
  const [settings, setSettings] = useState(null);
  const [userInput, setUserInput] = useState('');
  const [isRefining, setIsRefining] = useState(false);
  const [refinementTip, setRefinementTip] = useState('');
  const [updatingCards, setUpdatingCards] = useState(false);
  const [aiConfigured, setAiConfigured] = useState(aiService.isAIConfigured());
  const [showConfigModal, setShowConfigModal] = useState(false);
  const [apiKey, setApiKey] = useState('');
  const fileInputRef = useRef(null);

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    // Validate file type
    if (!file.type.startsWith('image/')) {
      alert('Please upload a valid image file.');
      return;
    }

    const imageUrl = URL.createObjectURL(file);
    setUploadedImage(imageUrl);
    setCurrentStep('analyzing');

    try {
      const analysisResult = await aiService.analyzeImage(file);
      setSettings(analysisResult);
      setCurrentStep('results');
    } catch (error) {
      console.error('Analysis failed:', error);
      alert('Sorry, we encountered an error analyzing your image. Please try again.');
      setCurrentStep('landing');
    }
  };

  const handleRefinement = async () => {
    if (!userInput.trim()) return;

    setIsRefining(true);
    setUpdatingCards(true);
    setRefinementTip('');

    try {
      const refinedSettings = await aiService.refineSettings(userInput, settings);

      // Show tip if present
      if (refinedSettings.tip) {
        setRefinementTip(refinedSettings.tip);
      }

      // Update settings with animation
      setTimeout(() => {
        setSettings(refinedSettings);
        setUpdatingCards(false);
        setUserInput('');
        setIsRefining(false);
      }, 500);

    } catch (error) {
      console.error('Refinement failed:', error);
      setIsRefining(false);
      setUpdatingCards(false);
      alert('Sorry, we encountered an error refining your settings. Please try again.');
    }
  };

  const handleConfigureAI = () => {
    setShowConfigModal(true);
  };

  const handleSaveAPIKey = () => {
    if (apiKey.trim()) {
      // In a real app, you'd want to store this securely
      // For now, we'll update the environment and reload
      localStorage.setItem('openai_api_key', apiKey);
      setAiConfigured(true);
      setShowConfigModal(false);
      setApiKey('');
      // Reload to pick up the new API key
      window.location.reload();
    }
  };

  const resetApp = () => {
    setCurrentStep('landing');
    setUploadedImage(null);
    setSettings(null);
    setUserInput('');
    setIsRefining(false);
    setRefinementTip('');
    setUpdatingCards(false);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Landing Page
  if (currentStep === 'landing') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-gray-100 font-inter">
        {/* Hero Background Image */}
        <div 
          className="absolute inset-0 bg-cover bg-center bg-no-repeat opacity-20"
          style={{
            backgroundImage: "url('https://images.unsplash.com/photo-1606983340126-99ab4feaa64a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2574&q=80')"
          }}
        />

        {/* Main Content */}
        <div className="relative z-10 min-h-screen flex items-center justify-center px-6">
          <div className="text-center max-w-2xl">
            <h1 className="text-5xl md:text-6xl font-bold text-gray-900 mb-8 leading-tight">
              Translate Your Phone's Photo into 
              <span className="text-accent block mt-2">DSLR Settings</span>
            </h1>

            <p className="text-xl text-gray-600 mb-12 leading-relaxed">
              Bridge the gap between smartphone photography and DSLR mastery. 
              Upload any photo and discover the perfect camera settings to recreate that shot.
            </p>

            {/* AI Configuration Banner */}
            {!aiConfigured && (
              <div className="mb-8">
                <AIConfigurationBanner onConfigure={handleConfigureAI} />
              </div>
            )}

            <button
              onClick={() => fileInputRef.current?.click()}
              className="inline-flex items-center justify-center px-12 py-6 bg-accent hover:bg-accent-hover text-white text-xl font-semibold rounded-2xl shadow-xl hover:shadow-2xl transform hover:scale-105 transition-all duration-200 focus:outline-none focus:ring-4 focus:ring-accent/25"
            >
              Upload a Photo to Start
            </button>

            <input
              ref={fileInputRef}
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="hidden"
            />
          </div>
        </div>

        {/* AI Configuration Modal */}
        {showConfigModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-6">
            <div className="bg-white rounded-2xl p-8 max-w-md w-full">
              <h3 className="text-2xl font-bold text-gray-900 mb-6">Configure AI Analysis</h3>
              <p className="text-gray-600 mb-6">
                Enter your OpenAI API key to enable intelligent image analysis and natural language refinement.
              </p>
              <div className="mb-6">
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  OpenAI API Key
                </label>
                <input
                  type="password"
                  value={apiKey}
                  onChange={(e) => setApiKey(e.target.value)}
                  placeholder="sk-..."
                  className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:border-accent focus:outline-none"
                />
              </div>
              <div className="flex space-x-4">
                <button
                  onClick={() => setShowConfigModal(false)}
                  className="flex-1 px-6 py-3 bg-gray-200 hover:bg-gray-300 text-gray-800 font-semibold rounded-xl transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={handleSaveAPIKey}
                  disabled={!apiKey.trim()}
                  className="flex-1 px-6 py-3 bg-accent hover:bg-accent-hover text-white font-semibold rounded-xl transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Save & Enable AI
                </button>
              </div>
              <div className="mt-4 text-xs text-gray-500">
                <a href="https://platform.openai.com/api-keys" target="_blank" rel="noopener noreferrer" className="text-accent hover:underline">
                  Get your API key from OpenAI →
                </a>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  }

  // Analysis Loading Page
  if (currentStep === 'analyzing') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-gray-100 font-inter flex items-center justify-center px-6">
        <div className="text-center max-w-md">
          <div className="mb-8">
            <LoadingSpinner />
          </div>
          <h2 className="text-2xl font-semibold text-gray-900 mb-4">
            {aiConfigured ? 'AI Analyzing Your Photo' : 'Analyzing Your Photo'}
          </h2>
          <p className="text-gray-600">
            {aiConfigured 
              ? 'Our AI is examining lighting conditions, subject matter, and composition to determine optimal DSLR settings...'
              : 'Analyzing your image to determine the optimal DSLR settings...'
            }
          </p>
        </div>
      </div>
    );
  }

  // Results Page
  if (currentStep === 'results') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-gray-100 font-inter py-12 px-6">
        <div className="max-w-6xl mx-auto">
          {/* Header */}
          <div className="text-center mb-12">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">
              Your DSLR Settings
            </h2>
            <p className="text-lg text-gray-600">
              {aiConfigured 
                ? 'AI-powered analysis of your photo with recommended camera settings'
                : 'Based on your photo analysis, here are the recommended camera settings'
              }
            </p>
          </div>

          {/* Settings Cards */}
          <div className="grid md:grid-cols-3 gap-8 mb-16">
            <SettingCard
              title="ISO"
              value={settings.iso}
              explanation={settings.explanations.iso}
              isUpdating={updatingCards}
              confidence={settings.confidence}
            />
            <SettingCard
              title="Aperture"
              value={settings.aperture}
              explanation={settings.explanations.aperture}
              isUpdating={updatingCards}
              confidence={settings.confidence}
            />
            <SettingCard
              title="Shutter Speed"
              value={settings.shutterSpeed}
              explanation={settings.explanations.shutterSpeed}
              isUpdating={updatingCards}
              confidence={settings.confidence}
            />
          </div>

          {/* Photography Type Info */}
          {settings.photographyType && (
            <div className="bg-blue-50 border-l-4 border-blue-400 p-6 mb-8 rounded-r-lg">
              <div className="flex items-center">
                <div className="text-blue-600 font-semibold">📸 Photography Type: </div>
                <div className="text-blue-800 ml-2 capitalize">{settings.photographyType}</div>
              </div>
            </div>
          )}

          {/* Refinement Tip */}
          {refinementTip && (
            <div className="bg-amber-50 border-l-4 border-amber-400 p-6 mb-8 rounded-r-lg">
              <div className="flex items-center">
                <div className="text-amber-600 font-semibold">💡 Pro Tip: </div>
                <div className="text-amber-800 ml-2">{refinementTip}</div>
              </div>
            </div>
          )}

          {/* Refinement Section */}
          <div className="bg-white rounded-2xl shadow-lg p-8 mb-8">
            <h3 className="text-2xl font-semibold text-gray-900 mb-6 text-center">
              Want to fine-tune these settings for a specific style?
            </h3>

            <div className="flex flex-col md:flex-row gap-4 max-w-2xl mx-auto">
              <input
                type="text"
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                placeholder={aiConfigured 
                  ? "e.g., 'Make everything blurry' or 'Keep everything sharp'"
                  : "e.g., 'blur background', 'freeze motion', 'smooth water'"
                }
                className="flex-1 px-6 py-4 border-2 border-gray-200 rounded-xl focus:border-accent focus:outline-none text-lg"
                onKeyPress={(e) => e.key === 'Enter' && !isRefining && handleRefinement()}
                disabled={isRefining}
              />
              <button
                onClick={handleRefinement}
                disabled={isRefining || !userInput.trim()}
                className="px-8 py-4 bg-accent hover:bg-accent-hover text-white font-semibold rounded-xl disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 whitespace-nowrap"
              >
                {isRefining ? 'Refining...' : 'Refine Settings'}
              </button>
            </div>

            <div className="mt-6 text-center text-sm text-gray-500">
              {aiConfigured 
                ? "Try natural language: 'everything blurry', 'freeze motion', 'dreamy effect', 'crisp details'"
                : "Try: 'everything blurry', 'everything sharp', 'blur background', 'freeze motion'"
              }
            </div>
          </div>

          {/* Action Buttons */}
          <div className="text-center space-x-4">
            <button
              onClick={() => fileInputRef.current?.click()}
              className="px-8 py-3 bg-gray-200 hover:bg-gray-300 text-gray-800 font-semibold rounded-xl transition-all duration-200"
            >
              Try Another Photo
            </button>
            <button
              onClick={resetApp}
              className="px-8 py-3 bg-accent hover:bg-accent-hover text-white font-semibold rounded-xl transition-all duration-200"
            >
              Start Over
            </button>
          </div>

          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="hidden"
          />
        </div>
      </div>
    );
  }

  return null;
};

export default App;
